(40, '../Images/khoiNghiep2.jpg', ' Thành triệu phú đô la nhờ bán dép in... twitter của ông Trump', 'Làm giàu không khó, quan trọng là bạn có đủ ý tưởng và can đảm hay không?\r\n', '', 11, 1, '2019-01-26 17:00:00', 1, 7, 0),
(49, '../Images/dienThoai.jpg', '10 triệu nên &quot;múc&quot; Galaxy Note 8 cũ hay iPhone 6s Plus mới tốt hơn?', 'Ở cùng một mức giá tiền, iPhone 6s Plus mới và Note 8 cũ đang là một trong những lựa chọn tốt nhất cho người dùng.', '', 13, 1, '2019-02-14 17:00:00', 1, 1, 0),
(50, '../Images/quanSu.jpg', 'Trung Quốc đừng &quot;đánh thức mãnh thú&quot; Mỹ', 'Tư lệnh Các chiến dịch hải quân Mỹ, Đô đốc John Richardson, sẽ thăm Bắc Kinh và TP Nam Kinh, đồng thời gặp gỡ người đồng cấp Trung Quốc là Phó Đô đốc Thẩm Kim Long và các lãnh đạo của Ủy ban Quân ủy trung ương Trung Quốc.', '', 6, 1, '2019-02-14 17:00:00', 1, 36, 0),
(52, '../Images/bongda3.jpg', 'Tin nóng cúp C1 19/2: Robben bị ám ảnh bởi Liverpool và Anfield', 'Trong một chia sẻ mới đây, Robben cho rằng anh từng bị Liverpool và sân vận động Anfield ám ảnh.', '', 1, 2, '2019-02-28 17:00:00', 1, 65, 5),
(53, '../Images/oto4.jpg', '\"Siêu xe giá rẻ\" Chevrolet Corvette rục rịch tăng giá dù không nâng cấp bất cứ thứ gì', 'Toàn bộ sản phẩm Corvette 2019 đều tăng giá bán, cao nhất là mẫu ZR1 với mức giá 2,8 tỷ đồng', '', 17, 1, '2019-02-20 17:00:00', 1, 4, 1);
INSERT INTO `tblbaiviet` (`maBaiViet`, `anh`, `tenBaiViet`, `moTa`, `noiDung`, `maTheLoaiCon`, `maUser`, `ngayDangBai`, `tinhTrangBv`, `luotXem`, `luotLuu`) VALUES
(55, '../Images/tuVan.jpg', 'Các hãng xe ô tô Trung Quốc đang cạnh tranh tại thị trường Việt Nam', 'Cung cấp thông tin về các thương hiệu ô tô đến từ Trung Quốc, các dòng xe hot đang bán chạy trên thị trường ô tô Việt.', '', 19, 1, '2019-02-23 17:00:00', 1, 32, 0),
(56, '../Images/giaCa.jpg', 'Hoa sen, chim phóng sinh... tăng giá mạnh dịp Rằm tháng Giêng', 'Tại TP HCM, nhu cầu đi chùa cúng sao giải hạn của người dân tăng cao kéo theo các dịch vụ bán nhang đèn, chim phóng sinh, hoa sen... cũng hút khách.', '', 20, 1, '2019-03-05 10:49:47', 1, 5, 0),
(57, '../Images/giaCaHangHoa1.jpg', 'Bộ Công thương lý giải việc giá điện tăng 8,36% trong tháng 3', '&quot;Đúng ra 2018 đã phải điều chỉnh rồi nhưng vì nhiều lý do ta điều chỉnh vào tháng 3/2019 với mức tăng 8,36%. Việc tăng giá điện sẽ được thực hiện trong tháng 3&quot;, Thứ trưởng Bộ Công Thương Hoàng Quốc Vượng lý giải và cho rằng việc tăng giá căn cứ vào giá thành, cơ cấu nguồn điện tăng (than, dầu, khí) cũng như các loại phí, chênh lệch tỷ giá.', '', 20, 3, '2019-03-08 04:42:35', 1, 4, 0);

